function Screen1_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileimage_2': 'Screen1_mobileimage_2',
        'spacer_3': 'Screen1_spacer_3',
        'mobilegroupedbuttons_14': 'Screen1_mobilegroupedbuttons_14',
        'mobilebutton_15': 'Screen1_mobilebutton_15',
        'mobilebutton_16': 'Screen1_mobilebutton_16',
        'mobilebutton_17': 'Screen1_mobilebutton_17'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'Screen1';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var Screen1_onLoad = function() {
            Screen1_elementsExtraJS();

            Screen1_deviceEvents();
            Screen1_windowEvents();
            Screen1_elementsEvents();
        };

    // screen window events


    function Screen1_windowEvents() {

        $('#Screen1').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function Screen1_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function Screen1_elementsExtraJS() {
        // screen (Screen1) extra code

    };

    // screen elements handler


    function Screen1_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#Screen1_mobilecontainer [name="mobilebutton_15"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    $url = 'http://feedbackhunter.esy.es/formulario/?f=' + $a.storage["resultado"].get($) + '&uuid=' + device.uuid;
                    window.open($url, '_blank', "location=yes,closebuttoncaption=Fechar, width='400', height='800,resizable=yes'");
                    Screen1_deviceEvents();
                    Screen1_windowEvents();
                    Screen1_elementsEvents();

                }
            },
        }, '#Screen1_mobilecontainer [name="mobilebutton_15"]');
        $(document).off("click", '#Screen1_mobilecontainer [name="mobilebutton_16"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    $url = 'http://feedbackhunter.esy.es/formulario/?f=1&uuid=' + device.uuid;
                    window.open($url, '_blank', "location=yes,closebuttoncaption=Fechar, width='400', height='800,resizable=yes'");
                    Screen1_deviceEvents();
                    Screen1_windowEvents();
                    Screen1_elementsEvents();

                }
            },
        }, '#Screen1_mobilecontainer [name="mobilebutton_16"]');
        $(document).off("click", '#Screen1_mobilecontainer [name="mobilebutton_17"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    window.open('http://feedbackhunter.esy.es', 'NewWindow', 'width=600,height=400,toolbar=yes,location=yes,directories=yes,status=yes,menubar=yes,scrollbars=yes,copyhistory=yes,resizable=yes');

                }
            },
        }, '#Screen1_mobilecontainer [name="mobilebutton_17"]');

    };

    $(document).off("pagebeforeshow", "#Screen1").on("pagebeforeshow", "#Screen1", function(event, ui) {
        Apperyio.CurrentScreen = "Screen1";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    Screen1_onLoad();
};

